import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import CookieBanner from './components/CookieBanner';
import { ThemeProvider } from './contexts/ThemeContext';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
axios.defaults.baseURL = API_URL;

function AppContent() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentView, setCurrentView] = useState("dashboard");
  const [user, setUser] = useState(null);

  // Forms
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [registerData, setRegisterData] = useState({ username: "", email: "", password: "" });

  // Groups + Files
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groupFiles, setGroupFiles] = useState([]);

  // Stats + Peers
  const [stats, setStats] = useState(null);
  const [peers, setPeers] = useState([]);

  // UI helpers
  const [notification, setNotification] = useState(null);
  const [loading, setLoading] = useState(false);

  const showNotification = (message, type = "success") => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  // Load user if token exists
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) return;

    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    setIsAuthenticated(true);
    loadUserData();
  }, []);

  const loadUserData = async () => {
    try {
      const [groupsRes, statsRes, peersRes] = await Promise.all([
        axios.get("/groups"),
        axios.get("/stats/dashboard"),
        axios.get("/peers")
      ]);

      setGroups(groupsRes.data);
      setStats(statsRes.data);
      setPeers(peersRes.data);

    } catch (err) {
      console.log("User load error:", err);
    }
  };

  // Login
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("/auth/login", loginData);
      localStorage.setItem("token", res.data.token);
      axios.defaults.headers.common["Authorization"] = `Bearer ${res.data.token}`;

      setUser(res.data.user);
      setIsAuthenticated(true);
      setCurrentView("dashboard");

      loadUserData();
      showNotification("Welcome back!");
    } catch (err) {
      showNotification("Login failed", "error");
    }
    setLoading(false);
  };

  // Register
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post("/auth/register", registerData);
      localStorage.setItem("token", res.data.token);
      axios.defaults.headers.common["Authorization"] = `Bearer ${res.data.token}`;

      setUser(res.data.user);
      setIsAuthenticated(true);
      setCurrentView("dashboard");

      loadUserData();
      showNotification("Account created!");
    } catch (err) {
      showNotification("Registration failed", "error");
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    setUser(null);
    setCurrentView("login");
  };

  // Load files of group
  const selectGroup = (g) => {
    setSelectedGroup(g);
    setCurrentView("groupfiles");
    loadGroupFiles(g.id);
  };

  const loadGroupFiles = async (id) => {
    try {
      const res = await axios.get(`/files/group/${id}`);
      setGroupFiles(res.data);
    } catch (err) {
      console.log("File load error");
    }
  };


  // ======================= AUTH UI =======================
  if (!isAuthenticated) {
    return (
      <div className="auth-wrapper">
        <div className="auth-card">
          <h1>DeCloud Storage</h1>

          <div className="auth-tabs">
            <button className={currentView === "login" ? "active" : ""} onClick={() => setCurrentView("login")}>
              Login
            </button>

            <button className={currentView === "register" ? "active" : ""} onClick={() => setCurrentView("register")}>
              Register
            </button>
          </div>

          {currentView === "login" ? (
            <form className="auth-form" onSubmit={handleLogin}>
              <input type="email" placeholder="Email" required
                value={loginData.email}
                onChange={(e) => setLoginData({ ...loginData, email: e.target.value })} />

              <input type="password" placeholder="Password" required
                value={loginData.password}
                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })} />

              <button>{loading ? "Loading..." : "Login"}</button>
            </form>
          ) : (
            <form className="auth-form" onSubmit={handleRegister}>
              <input type="text" placeholder="Username" required
                value={registerData.username}
                onChange={(e) => setRegisterData({ ...registerData, username: e.target.value })} />

              <input type="email" placeholder="Email" required
                value={registerData.email}
                onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })} />

              <input type="password" placeholder="Password" required
                value={registerData.password}
                onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })} />

              <button>{loading ? "Loading..." : "Register"}</button>
            </form>
          )}
        </div>

        {notification && (
          <div className={`notification ${notification.type}`}>{notification.message}</div>
        )}
      </div>
    );
  }

  // ======================= MAIN APP UI =======================
  return (
    <div className="app-layout">

      {/* FIXED NAVBAR */}
      <Navbar user={user} onLogout={handleLogout} />

      <div className="layout-body">

        {/* SIDEBAR */}
        <aside className="sidebar">
          <h3>Navigation</h3>

          <button className={currentView === "dashboard" ? "active" : ""} onClick={() => setCurrentView("dashboard")}>
            ðŸ“Š Dashboard
          </button>

          <button className={currentView === "groups" ? "active" : ""} onClick={() => setCurrentView("groups")}>
            ðŸ‘¥ My Groups
          </button>

          <button className={currentView === "creategroup" ? "active" : ""} onClick={() => setCurrentView("creategroup")}>
            âž• Create Group
          </button>

          <button className={currentView === "joingroup" ? "active" : ""} onClick={() => setCurrentView("joingroup")}>
            ðŸ”— Join Group
          </button>

          <button className={currentView === "peers" ? "active" : ""} onClick={() => setCurrentView("peers")}>
            ðŸ’» Peers
          </button>
        </aside>

        {/* MAIN CONTENT */}
        <main className="main-content">
          {/* Dashboard */}
          {currentView === "dashboard" && stats && (
            <div className="dashboard-container">
              <h1>Dashboard Overview</h1>

              <div className="stats-grid">
                <div className="stat-box">
                  <h3>Storage Used</h3>
                  <p>{(stats.storageUsed / 1024 / 1024).toFixed(2)} MB</p>
                </div>

                <div className="stat-box">
                  <h3>Total Files</h3>
                  <p>{stats.fileCount}</p>
                </div>

                <div className="stat-box">
                  <h3>Groups</h3>
                  <p>{stats.groupCount}</p>
                </div>

                <div className="stat-box">
                  <h3>Peers Online</h3>
                  <p>{stats.peersOnline}</p>
                </div>
              </div>
            </div>
          )}
        </main>
      </div>

      {/* FOOTER */}
      <Footer />

      <CookieBanner user={user} />
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
