// CookieBanner.js
import React, { useEffect, useState } from "react";

export default function CookieBanner() {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    try {
      const accepted = localStorage.getItem("decloud_cookies_accepted");
      if (!accepted) setVisible(true);
    } catch (e) {
      setVisible(true); // show if localStorage fails
    }
  }, []);

  const accept = () => {
    try {
      localStorage.setItem("decloud_cookies_accepted", "1");
    } catch (e) {}
    setVisible(false);
  };

  if (!visible) return null;

  return (
    <div
      className="cookie-banner"
      style={{
        position: "fixed",
        left: 12,
        right: 12,
        bottom: 12,
        padding: "1rem 1.25rem",
        background: "var(--surface, #fff)",
        boxShadow: "0 6px 18px rgba(0,0,0,0.08)",
        borderRadius: 10,
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        gap: 12,
        zIndex: 9999,
      }}
    >
      <div style={{ flex: 1 }}>
        <strong>We use cookies</strong>
        <div style={{ fontSize: 13, marginTop: 4 }}>
          We use cookies to improve your experience and to provide certain features.
          By continuing you accept our{" "}
          <a href="/privacy" onClick={(e) => e.preventDefault()}>
            privacy policy
          </a>
          .
        </div>
      </div>

      <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
        <button
          onClick={accept}
          style={{
            padding: "0.5rem 0.9rem",
            borderRadius: 8,
            border: "none",
            cursor: "pointer",
          }}
        >
          Accept
        </button>
        <button
          onClick={() => setVisible(false)}
          style={{
            padding: "0.4rem 0.7rem",
            borderRadius: 8,
            border: "1px solid rgba(0,0,0,0.08)",
            background: "transparent",
            cursor: "pointer",
          }}
        >
          Dismiss
        </button>
      </div>
    </div>
  );
}
