import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ThemeProvider } from './contexts/ThemeContext';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import CookieBanner from './components/CookieBanner';
import './App.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
axios.defaults.baseURL = API_URL;

function AppContent() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentView, setCurrentView] = useState('login');
  const [user, setUser] = useState(null);
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [groupFiles, setGroupFiles] = useState([]);
  const [peers, setPeers] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [notification, setNotification] = useState(null);

  // Auth Form States
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ username: '', email: '', password: '' });

  // Group Form States
  const [createGroupData, setCreateGroupData] = useState({ name: '', description: '' });
  const [joinGroupCode, setJoinGroupCode] = useState('');

  // File Upload States
  const [uploadFile, setUploadFile] = useState(null);
  const [uploadTags, setUploadTags] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      setIsAuthenticated(true);
      loadUserData();
    }
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      const interval = setInterval(() => {
        loadGroups();
        loadPeers();
        if (selectedGroup) {
          loadGroupFiles(selectedGroup.id);
        }
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [isAuthenticated, selectedGroup]);

  const showNotification = (message, type = 'success') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  // ==================== AUTH FUNCTIONS ====================

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('/auth/login', loginData);
      localStorage.setItem('token', response.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.token}`;
      setIsAuthenticated(true);
      setUser(response.data.user);
      setCurrentView('dashboard');
      showNotification('Welcome back!');
      loadUserData();
    } catch (error) {
      showNotification(error.response?.data?.error || 'Login failed', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('/auth/register', registerData);
      localStorage.setItem('token', response.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.token}`;
      setIsAuthenticated(true);
      setUser(response.data.user);
      setCurrentView('dashboard');
      showNotification('Account created successfully!');
      loadUserData();
    } catch (error) {
      showNotification(error.response?.data?.error || 'Registration failed', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setCurrentView('login');
    showNotification('Logged out successfully');
  };

  const loadUserData = async () => {
    try {
      const [groupsRes, statsRes, peersRes] = await Promise.all([
        axios.get('/groups'),
        axios.get('/stats/dashboard'),
        axios.get('/peers')
      ]);
      setGroups(groupsRes.data);
      setStats(statsRes.data);
      setPeers(peersRes.data);
    } catch (error) {
      console.error('Error loading user data:', error);
    }
  };

  // ==================== GROUP FUNCTIONS ====================

  const loadGroups = async () => {
    try {
      const response = await axios.get('/groups');
      setGroups(response.data);
    } catch (error) {
      console.error('Error loading groups:', error);
    }
  };

  const handleCreateGroup = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('/groups/create', createGroupData);
      showNotification(`Group created! Invite code: ${response.data.group.inviteCode}`);
      setCreateGroupData({ name: '', description: '' });
      loadGroups();
      setCurrentView('groups');
    } catch (error) {
      showNotification(error.response?.data?.error || 'Failed to create group', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleJoinGroup = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('/groups/join', { inviteCode: joinGroupCode });
      showNotification('Joined group successfully!');
      setJoinGroupCode('');
      loadGroups();
      setCurrentView('groups');
    } catch (error) {
      showNotification(error.response?.data?.error || 'Failed to join group', 'error');
    } finally {
      setLoading(false);
    }
  };

  const selectGroup = async (group) => {
    setSelectedGroup(group);
    setCurrentView('groupfiles');
    loadGroupFiles(group.id);
  };

  const loadGroupFiles = async (groupId) => {
    try {
      const response = await axios.get(`/files/group/${groupId}`);
      setGroupFiles(response.data);
    } catch (error) {
      console.error('Error loading group files:', error);
    }
  };

  // ==================== FILE FUNCTIONS ====================

  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!uploadFile) {
      showNotification('Please select a file', 'error');
      return;
    }
    if (!selectedGroup) {
      showNotification('Please select a group first', 'error');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('file', uploadFile);
    formData.append('groupId', selectedGroup.id);
    formData.append('tags', JSON.stringify(uploadTags.split(',').map(t => t.trim()).filter(t => t)));

    try {
      await axios.post('/files/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      showNotification('File uploaded and encrypted!');
      setUploadFile(null);
      setUploadTags('');
      loadGroupFiles(selectedGroup.id);
      loadUserData();
    } catch (error) {
      showNotification(error.response?.data?.error || 'Upload failed', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleFileDownload = async (fileId, filename) => {
    try {
      showNotification('Decrypting and downloading...');
      const response = await axios.get(`/files/download/${fileId}`, {
        responseType: 'blob'
      });
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
      showNotification('Download complete!');
    } catch (error) {
      showNotification(error.response?.data?.error || 'Download failed', 'error');
    }
  };

  const handleFileDelete = async (fileId) => {
    if (!window.confirm('Are you sure you want to delete this file?')) return;

    try {
      await axios.delete(`/files/${fileId}`);
      showNotification('File deleted successfully!');
      loadGroupFiles(selectedGroup.id);
      loadUserData();
    } catch (error) {
      showNotification('Delete failed', 'error');
    }
  };

  const loadPeers = async () => {
    try {
      const response = await axios.get('/peers');
      setPeers(response.data);
    } catch (error) {
      console.error('Error loading peers:', error);
    }
  };

  // ==================== UTILITY FUNCTIONS ====================

  const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + ' B';
    if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(2) + ' KB';
    if (bytes < 1024 * 1024 * 1024) return (bytes / (1024 * 1024)).toFixed(2) + ' MB';
    return (bytes / (1024 * 1024 * 1024)).toFixed(2) + ' GB';
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  // ==================== RENDER AUTH ====================

  if (!isAuthenticated) {
    return (
      <div className="auth-container">
        <div className="auth-card">
          <h1>ğŸŒ Decentralized Cloud Storage</h1>
          <p style={{textAlign: 'center', color: '#666', marginBottom: '20px'}}>
            Secure Group-Based File Sharing with End-to-End Encryption
          </p>
          <div className="auth-tabs">
            <button
              className={currentView === 'login' ? 'active' : ''}
              onClick={() => setCurrentView('login')}
            >
              Login
            </button>
            <button
              className={currentView === 'register' ? 'active' : ''}
              onClick={() => setCurrentView('register')}
            >
              Register
            </button>
          </div>

          {currentView === 'login' ? (
            <form onSubmit={handleLogin} className="auth-form">
              <input
                type="email"
                placeholder="Email"
                value={loginData.email}
                onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={loginData.password}
                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                required
              />
              <button type="submit" disabled={loading}>
                {loading ? 'Loading...' : 'Login'}
              </button>
            </form>
          ) : (
            <form onSubmit={handleRegister} className="auth-form">
              <input
                type="text"
                placeholder="Username"
                value={registerData.username}
                onChange={(e) => setRegisterData({ ...registerData, username: e.target.value })}
                required
              />
              <input
                type="email"
                placeholder="Email"
                value={registerData.email}
                onChange={(e) => setRegisterData({ ...registerData, email: e.target.value })}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={registerData.password}
                onChange={(e) => setRegisterData({ ...registerData, password: e.target.value })}
                required
              />
              <button type="submit" disabled={loading}>
                {loading ? 'Loading...' : 'Register'}
              </button>
            </form>
          )}
        </div>

        {notification && (
          <div className={`notification ${notification.type}`}>
            {notification.message}
          </div>
        )}
      </div>
    );
  }

  // ==================== RENDER MAIN APP ====================

  return (
    <div className="app-wrapper">
      <Navbar user={user} onLogout={handleLogout} />
      
      <div className="app-container">
        {/* Sidebar */}
        <div className="sidebar">
          <h2>ğŸ“ Navigation</h2>
          <nav>
            <button
              className={currentView === 'dashboard' ? 'active' : ''}
              onClick={() => setCurrentView('dashboard')}
            >
              ğŸ“Š Dashboard
            </button>
            <button
              className={currentView === 'groups' ? 'active' : ''}
              onClick={() => setCurrentView('groups')}
            >
              ğŸ‘¥ My Groups ({groups.length})
            </button>
            <button
              className={currentView === 'creategroup' ? 'active' : ''}
              onClick={() => setCurrentView('creategroup')}
            >
              â• Create Group
            </button>
            <button
              className={currentView === 'joingroup' ? 'active' : ''}
              onClick={() => setCurrentView('joingroup')}
            >
              ğŸ”— Join Group
            </button>
            {selectedGroup && (
              <button
                className={currentView === 'groupfiles' ? 'active' : ''}
                onClick={() => setCurrentView('groupfiles')}
              >
                ğŸ“ {selectedGroup.name}
              </button>
            )}
            <button
              className={currentView === 'peers' ? 'active' : ''}
              onClick={() => setCurrentView('peers')}
            >
              ğŸ’» Peers ({peers.length})
            </button>
          </nav>
        </div>

        {/* Main Content */}
        <div className="main-content">
          {/* Dashboard View */}
          {currentView === 'dashboard' && stats && (
            <div className="dashboard">
              <h1>Dashboard</h1>
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>Storage Used</h3>
                  <p className="stat-value">{formatFileSize(stats.storageUsed)}</p>
                  <p className="stat-label">of {formatFileSize(stats.storageLimit)}</p>
                  <div className="progress-bar">
                    <div
                      className="progress-fill"
                      style={{ width: `${(stats.storageUsed / stats.storageLimit) * 100}%` }}
                    ></div>
                  </div>
                </div>
                <div className="stat-card">
                  <h3>Total Groups</h3>
                  <p className="stat-value">{stats.groupCount}</p>
                </div>
                <div className="stat-card">
                  <h3>Total Files</h3>
                  <p className="stat-value">{stats.fileCount}</p>
                </div>
                <div className="stat-card">
                  <h3>Peers Online</h3>
                  <p className="stat-value">{stats.peersOnline}</p>
                </div>
              </div>

              <div className="recent-activity">
                <h2>Recent Activity</h2>
                <div className="activity-list">
                  {stats.recentTransfers && stats.recentTransfers.length > 0 ? (
                    stats.recentTransfers.map((transfer, index) => (
                      <div key={index} className="activity-item">
                        <span className="activity-icon">
                          {transfer.action === 'upload' ? 'ğŸ“¤' : 
                           transfer.action === 'download' ? 'ğŸ“¥' : 'ğŸ—‘ï¸'}
                        </span>
                        <div className="activity-details">
                          <p>{transfer.action} - {transfer.fileId?.originalName || 'Unknown'}</p>
                          <p className="activity-time">
                            Group: {transfer.groupId?.name} â€¢ {formatDate(transfer.timestamp)}
                          </p>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p>No recent activity</p>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Groups, Create Group, Join Group, Group Files Views - Keep your existing code */}
          {/* ... (Rest of your views remain same as before) ... */}
          
          {/* Peers View */}
          {currentView === 'peers' && (
            <div className="peers-view">
              <h1>Connected Peers</h1>
              <p className="view-description">Devices connected on the same network</p>
              <div className="peers-grid">
                {peers.length > 0 ? (
                  peers.map((peer, index) => (
                    <div key={index} className="peer-card">
                      <div className="peer-icon">ğŸ’»</div>
                      <h3>{peer.name}</h3>
                      <p>IP: {peer.ip}:{peer.port}</p>
                      <p className="peer-status">
                        <span className="status-dot online"></span> Online
                      </p>
                      <p className="peer-last-seen">Last seen: {formatDate(peer.lastSeen)}</p>
                    </div>
                  ))
                ) : (
                  <div className="empty-state">
                    <p>ğŸ” No peers discovered yet.</p>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>

      <Footer />
      <CookieBanner user={user} />

      {/* Notification */}
      {notification && (
        <div className={`notification ${notification.type}`}>
          {notification.message}
        </div>
      )}
    </div>
  );
}

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;S
