import React, { useState } from 'react';
import { useTheme } from '../contexts/ThemeContext';
import ThemeToggle from './ThemeToggle';

const Navbar = ({ user, onLogout }) => {
  const { theme } = useTheme();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <div className="logo">
          <span className="logo-icon">ğŸŒ</span>
          <span className="logo-text">DeCloud</span>
        </div>
        <span className="tagline">Decentralized Storage</span>
      </div>

      <div className="navbar-center">
        <div className="search-box-nav">
          <input type="text" placeholder="Search files..." />
          <button>ğŸ”</button>
        </div>
      </div>

      <div className="navbar-actions">
        <ThemeToggle />
        
        <div className="user-menu" onClick={() => setDropdownOpen(!dropdownOpen)}>
          <div className="user-avatar">
            <span>{user?.username?.charAt(0).toUpperCase()}</span>
          </div>
          <span className="username">{user?.username}</span>
          <span className="dropdown-arrow">â–¼</span>
          
          {dropdownOpen && (
            <div className="dropdown-menu">
              <div className="dropdown-item">
                <span>ğŸ‘¤ Profile</span>
              </div>
              <div className="dropdown-item">
                <span>âš™ï¸ Settings</span>
              </div>
              <div className="dropdown-item">
                <span>ğŸ“Š Storage: {formatStorage(user?.storageUsed || 0)}</span>
              </div>
              <div className="dropdown-divider"></div>
              <div className="dropdown-item" onClick={onLogout}>
                <span>ğŸšª Logout</span>
              </div>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

const formatStorage = (bytes) => {
  if (bytes < 1024) return bytes + ' B';
  if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
  if (bytes < 1024 * 1024 * 1024) return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
  return (bytes / (1024 * 1024 * 1024)).toFixed(2) + ' GB';
};

export default Navbar;
